package com.assignment.july26;

public class Swap {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 10;
		int b = 20;
		int temp;
		System.out.println("Before swapping: a = "+ a + " and b = " + b);
		temp = a;
		a = b;
		b = temp;
		System.out.println("After swapping: a = "+ a + " and b = " + b);
	}
}
